# -*- coding: utf-8 -*-import numpy as npfrom scipy import *import matplotlib.pyplot as pltQ = np.array([3.189, 1.895, 3.184, 4.721, 3.143])*1e-19e = 1.602*1e-19n = np.round(Q / e, 2)print(n)e_average = Q / ny1 = polyfit(n, Q, 1)y1 = np.poly1d(y1)print(y1)print(np.average(e_average))xx = linspace(0, 5)fig = plt.figure(dpi=250)y2 = str(y1)plt.plot(xx, polyval(y1, xx), label=y2)plt.plot(n, Q, '*')plt.xlabel('n')plt.ylabel('Q')plt.legend(loc='upper left', fontsize='small')plt.tight_layout()plt.show()plt.savefig('1.png', bbox_inches='tight')